import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:flutter_svg/flutter_svg.dart';import 'package:willow_care_app/theme/text_themes.dart';/// we put this app bar in most of app screens , where you can set different title for every screen top barclass AppBarLayout extends StatelessWidget {  final String title;  const AppBarLayout({Key? key, required this.title}) : super(key: key);  @override  Widget build(BuildContext context) {    return Container(      height: 27.h,      width: 328.w,      margin: EdgeInsets.only(top: 44.h, left: 24.w, right: 24.w),      child: Row(        mainAxisAlignment: MainAxisAlignment.spaceBetween,        children: [          Image.asset('assets/images/menu.png', height: 20.h, width: 20.h),          Text(title, style: TextThemes.appBarTextStyle),          SvgPicture.asset('assets/svgs/settings.svg',              height: 20.h, width: 20.h),        ],      ),    );  }}