import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:willow_care_app/common/layouts/rounded_container.dart';import 'package:willow_care_app/theme/app_colors.dart';import 'package:willow_care_app/theme/text_themes.dart';/// we use this layout for title of every category in doctor profile screenclass CustomCardInfoLayout extends StatelessWidget {  final String title;  final String icon;  const CustomCardInfoLayout(      {Key? key, required this.title, required this.icon})      : super(key: key);  @override  Widget build(BuildContext context) {    return Container(      height: 64.h,      width: 364,      margin: EdgeInsets.only(top: 24.h, right: 24.w, left: 24.w),      padding: EdgeInsets.symmetric(horizontal: 16.w),      decoration: BoxDecoration(          boxShadow: <BoxShadow>[            BoxShadow(              color: AppColors.cardShadowColor,              blurRadius: 20,              offset: const Offset(0, 0),            ),          ],          borderRadius: BorderRadius.all(Radius.circular(8.r)),          color: Colors.white),      child: Row(        children: [          Expanded(              flex: 2,              child: CircleAvatar(                  backgroundColor:                      AppColors.lightBlueBackgroundButtonColor.withOpacity(0.2),                  radius: 20.r,                  child: Image.asset(icon))),          Expanded(              flex: 6,              child: Text(title,                  style: TextThemes.cardSubTitleTextStyle                      .copyWith(fontWeight: FontWeight.bold))),          Expanded(            flex: 2,            child: SizedBox(                child: RoundedContainer(              height: 23.w,              width: 56,              radius: 12,              color: AppColors.lightBlueBackgroundButtonColor.withOpacity(0.2),              child: Center(                  child: Text(                'Edit',                style: TextThemes.cardSmallTitleTextStyle.copyWith(                  color: AppColors.lightBlueBackgroundButtonColor,                ),              )),            )),          )        ],      ),    );  }}