import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:willow_care_app/theme/text_themes.dart';/// we use this layout to design tabs in most of screensclass CustomTabItemLayout extends StatelessWidget {  final String title;  final bool isSelected;  final Function? onTab;  const CustomTabItemLayout(      {Key? key, required this.title, this.isSelected = false, this.onTab})      : super(key: key);  @override  Widget build(BuildContext context) {    return GestureDetector(      onTap: () {        onTab == null ? null : onTab!();      },      child: Container(        width: 157.w,        height: 42.h,        decoration: BoxDecoration(            borderRadius: BorderRadius.all(Radius.circular(7.r)),            color: isSelected ? Colors.white : Colors.transparent),        child: Center(          child: Text(            title,            style: isSelected                ? TextThemes.activeTextStyle                : TextThemes.inActiveTextStyle,          ),        ),      ),    );  }}