import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:willow_care_app/controllers/schedule_tab/schedule_tab_cubit.dart';import 'package:willow_care_app/theme/app_colors.dart';import 'custom_tab_item_layout.dart';/// we use this layout to add tabs in some page list schedule pageclass CustomTabLayout extends StatelessWidget {  final String tab1;  final String tab2;  const CustomTabLayout({Key? key, required this.tab1, required this.tab2})      : super(key: key);  @override  Widget build(BuildContext context) {    final tabBloc = BlocProvider.of<ScheduleTabCubit>(context);    return BlocBuilder<ScheduleTabCubit, int>(        bloc: tabBloc,        builder: (context, tabNum) {          return Container(            width: 327.w,            height: 50.h,            margin: EdgeInsets.only(top: 24.h),            decoration: BoxDecoration(                borderRadius: BorderRadius.all(Radius.circular(4.r)),                color: AppColors.scheduleTabBackgroundColor),            child: Row(              mainAxisAlignment: MainAxisAlignment.center,              children: [                CustomTabItemLayout(                  title: tab1,                  isSelected: tabNum == 0 ? true : false,                  onTab: () {                    tabBloc.changeScheduleTab(0);                  },                ),                const SizedBox(width: 5.4),                CustomTabItemLayout(                  title: tab2,                  isSelected: tabNum == 1 ? true : false,                  onTab: () {                    tabBloc.changeScheduleTab(1);                  },                )              ],            ),          );        });  }}