import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:willow_care_app/theme/app_colors.dart';///we use this layout to design most of app cards and buttonsclass RoundedContainer extends StatelessWidget {  final double width;  final double height;  final Widget? child;  final Color? color;  final Function? onTap;  final double radius;  const RoundedContainer({    Key? key,    required this.height,    required this.width,    this.child,    this.onTap,    this.color,    this.radius = 4,  }) : super(key: key);  @override  Widget build(BuildContext context) {    final _containerDecoration = BoxDecoration(        borderRadius: BorderRadius.all(Radius.circular(radius)),        color: color ?? AppColors.primaryColor);    return GestureDetector(      onTap: () {        onTap == null ? null : onTap!();      },      child: Container(        width: width.w,        height: height.h,        decoration: _containerDecoration,        child: child,      ),    );  }}