import 'package:bloc/bloc.dart';import 'package:dartz/dartz.dart';import 'package:meta/meta.dart';import 'package:willow_care_app/model/app_error.dart';import 'package:willow_care_app/model/schedule_model.dart';import 'package:willow_care_app/services/schedules_api.dart';part 'schedule_state.dart';/// we use this controller for getting data for api ahd display it in schedule screenclass ScheduleCubit extends Cubit<ScheduleState> {  final ScheduleApi _scheduleApi;  ScheduleCubit(this._scheduleApi) : super(ScheduleInitial());  getAllSchedules() async {    emit(ScheduleLoading());    Either<AppError, List<ScheduleModel>> scheduleResponse =        await _scheduleApi.getAllSchedules();    scheduleResponse.fold(      (l) => emit(ScheduleError(message: 'some thing went wrong')),      (r) => emit(        ScheduleSuccess(schedules: r),      ),    );  }}