import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:willow_care_app/screens/app/app_screen.dart';class DoctorProfileAppBar extends StatelessWidget {  const DoctorProfileAppBar({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return Container(      height: 24.h,      margin: EdgeInsets.only(top: 44.h, left: 24.w, right: 24.w),      child: Center(        child: Row(          children: [            GestureDetector(              onTap: () {                Navigator.pushReplacementNamed(context, AppScreen.routeName);              },              child: Icon(                Icons.arrow_back,                color: Colors.white,                size: 24.r,              ),            ),            const Spacer(),            Icon(              Icons.date_range_outlined,              color: Colors.white,              size: 24.r,            ),            SizedBox(width: 20.w),            Icon(              Icons.notifications,              color: Colors.white,              size: 24.r,            ),          ],        ),      ),    );  }}