import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:willow_care_app/theme/text_themes.dart';class DoctorProfileStatisticItemWidget extends StatelessWidget {  final String title;  final int number;  const DoctorProfileStatisticItemWidget(      {Key? key, required this.title, this.number = 0})      : super(key: key);  @override  Widget build(BuildContext context) {    return Column(      children: [        Text(title, style: TextThemes.cardSmallTitleTextStyle),        SizedBox(height: 5.h),        Text(number.toString(), style: TextThemes.activeTextStyle),      ],    );  }}