import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:willow_care_app/theme/text_themes.dart';class InsuranceCompanyListWidget extends StatelessWidget {  final List<String> items;  const InsuranceCompanyListWidget({Key? key, this.items = const []})      : super(key: key);  @override  Widget build(BuildContext context) {    return SizedBox(        height: 100.h,        child: ListView.builder(          padding: EdgeInsets.symmetric(horizontal: 24.w, vertical: 16.h),          physics: const NeverScrollableScrollPhysics(),          itemCount: items.length,          itemBuilder: (context, index) {            return Text(items[index],                style: TextThemes.cardSubTitleTextStyle                    .copyWith(fontWeight: FontWeight.bold));          },        ));  }}