import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:willow_care_app/controllers/schedule_tab/schedule_tab_cubit.dart';class ScheduleDateWidget extends StatelessWidget {  const ScheduleDateWidget({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return BlocBuilder<ScheduleTabCubit, int>(builder: (context, tabNum) {      return tabNum == 0          ? const SizedBox()          : Container(              height: 30.h,              width: double.infinity,              color: Colors.white,              margin: EdgeInsets.symmetric(vertical: 16.w),              padding: EdgeInsets.only(left: 20.w, right: 20.w, top: 7.h),              child: Text(                'This Week',                style: TextStyle(                  fontSize: 14.sp,                ),              ),            );    });  }}