import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:willow_care_app/controllers/schedule/schedule_cubit.dart';import 'package:willow_care_app/controllers/schedule_tab/schedule_tab_cubit.dart';import 'package:willow_care_app/screens/schedule/widget/schedule_list_item_widget.dart';import 'package:willow_care_app/theme/app_colors.dart';class ScheduleListWidget extends StatelessWidget {  const ScheduleListWidget({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    return BlocBuilder<ScheduleTabCubit, int>(      builder: (context, tabNum) {        return tabNum == 0            ? Expanded(                child: Center(                    child: Padding(                  padding: EdgeInsets.only(right: 30.0.w),                  child: Image.asset(                    'assets/images/no_schedule.png',                    width: 220.w,                    height: 162.h,                  ),                )),              )            : Expanded(                child: BlocBuilder<ScheduleCubit, ScheduleState>(                    builder: (context, state) {                  if (state is ScheduleSuccess) {                    return ListView.builder(                      padding: EdgeInsets.only(top: 5.h),                      itemCount: state.schedules.length,                      itemBuilder: (context, index) {                        return ScheduleListItemWidget(                          schedule: state.schedules[index],                        );                      },                    );                  }                  if (state is ScheduleLoading) {                    return Center(                      child: CircularProgressIndicator(                          color: AppColors.primaryColor),                    );                  }                  return const SizedBox();                }),              );      },    );  }}