import 'package:dartz/dartz.dart';import 'package:willow_care_app/common/constant/schedules.dart';import 'package:willow_care_app/model/app_error.dart';import 'package:willow_care_app/model/schedule_model.dart';/// this is the service that fetch schedules data from serverclass ScheduleApi {  Future<Either<AppError, List<ScheduleModel>>> getAllSchedules() async {    await Future.delayed(const Duration(seconds: 3));    return Right(Schedules.schedules);  }}